// src/components/ComingSoonBanner.js

import React, { useState } from 'react';
import { motion } from 'framer-motion';
import './ComingSoonBanner.css';

const ComingSoonBanner = () => {
  const [formData, setFormData] = useState({ name: '', email: '', phone: '' });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted:', formData);
    alert('Thanks! Weâ€™ll notify you when we launch.');
    setFormData({ name: '', email: '', phone: '' });
  };

  return (
    <motion.div
      className="coming-soon-container"
      initial={{ opacity: 0, y: 50 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.8 }}
    >
      <motion.h1
        className="coming-title"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.4 }}
      >
        ðŸš€ Coming Soon
      </motion.h1>
      <p className="coming-desc">Our platform is almost ready. Be the first to know when we launch.</p>

      <form className="coming-form" onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Your Name"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email Address"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <input
          type="tel"
          name="phone"
          placeholder="Phone Number"
          value={formData.phone}
          onChange={handleChange}
          required
        />
        <button type="submit">Notify Me</button>
      </form>
    </motion.div>
  );
};

export default ComingSoonBanner;
